# Selenium Test Script for TC_LOGIN_015: SQL Injection Prevention on Login\nimport pytest\nfrom selenium import webdriver\nfrom selenium.webdriver.chrome.options import Options\nfrom auto_scripts.Pages.LoginPage import LoginPage\n\n@pytest.fixture(scope=\"module\")\ndef driver():\n    options = Options()\n    options.add_argument('--headless')\n    options.add_argument('--disable-gpu')\n    driver = webdriver.Chrome(options=options)\n    driver.implicitly_wait(10)\n    yield driver\n    driver.quit()\n\ndef test_login_sql_injection_prevention(driver):\n    \"\"\"\n    TC_LOGIN_015: Test SQL Injection Prevention on Login\n    Steps:\n      1. Navigate to the login page\n      2. Enter valid email address\n      3. Enter SQL injection payload in password field\n      4. Click on the Login button\n      5. Verify system security (no unauthorized access, error displayed)\n    \"\"\"\n    # Test Data\n    email = \"testuser@example.com\"\n    sql_payload = "' OR '1'='1"\n\n    login_page = LoginPage(driver)\n    login_page.go_to_login_page()\n    assert login_page.is_login_fields_visible(), \"Login fields are not visible!\"\n    assert login_page.enter_email(email), \"Email was not entered correctly!\"\n    password_field = driver.find_element(*LoginPage.PASSWORD_FIELD)\n    password_field.clear()\n    password_field.send_keys(sql_payload)\n    login_page.click_login()\n\n    import time\n    time.sleep(1)  # Wait for error message to appear\n    from selenium.common.exceptions import NoSuchElementException\n    try:\n        error = driver.find_element(*LoginPage.ERROR_MESSAGE)\n        assert error.is_displayed(), \"Error message not displayed!\"\n    except NoSuchElementException:\n        pytest.fail(\"Expected error message not found!\")\n\n    assert not login_page.is_redirected_to_dashboard(), \"SQL injection succeeded! Unauthorized access granted.\"\n